mapper.select:
SELECT COUNT(*) AS `sumcount`, (SELECT COUNT(*) FROM `crmdata` WHERE source LIKE(${source}) AND firsttime>#{starttime} AND firsttime < #{endtime} AND stat=-1 ) AS validcount , (SELECT COUNT(*) FROM `crmdata` WHERE source LIKE(${source}) AND firsttime>#{starttime} AND firsttime < #{endtime} AND datasource='接口提交' ) AS apicount,
(SELECT COUNT(*) FROM `crmdata` WHERE source LIKE(${source}) AND firsttime>#{starttime} AND firsttime < #{endtime} AND datasource='客户录入' ) AS servicecount
FROM `crmdata` WHERE source LIKE(${source}) AND firsttime>#{starttime} AND firsttime < #{endtime}

SELECT COUNT(*) AS `sumcount`, (SELECT COUNT(*) FROM `crmdata` WHERE firsttime>#{starttime} AND firsttime < #{endtime} AND stat=-1 ) AS validcount , (SELECT COUNT(*) FROM `crmdata` WHERE firsttime>#{starttime} AND firsttime < #{endtime} AND datasource='接口提交' ) AS apicount,
(SELECT COUNT(*) FROM `crmdata` WHERE firsttime>#{starttime} AND firsttime < #{endtime} AND datasource='客户录入' ) AS servicecount
FROM `crmdata` WHERE firsttime>#{starttime} AND firsttime < #{endtime}
